mapper.select:
    SELECT
		co.order_code AS orderCode,
		co.grand_total AS grandTotal,
		co.create_time AS orderTime,
		co.product_count AS productCount,
		co.order_status AS orderStatus
	FROM
		client_order co
	
		co.`status` != 0
		
			and co.user_code = #{userCode}
		
		
			and co.create_time >= #{orderStartTime}
		
		
			and co.create_time <= #{orderEndTime}
		
		
			and co.order_code = #{searchInput}

SELECT co.order_code AS orderCode, co.create_time AS orderTime, co.grand_total AS grandTotal, co.order_status AS orderStatus, pu.user_name AS salesman FROM client_order co JOIN platform_user_salesman pus ON co.salesman_code = pus.salesman_code JOIN platform_user pu ON pu.user_code = pus.platform_user_code
co.`status` != 0
and co.create_time >= #{orderStartTime}
and co.create_time <= #{orderEndTime}
and ( pu.user_name LIKE CONCAT('%', #{searchInput}, '%') or co.order_code = #{searchInput} )
ORDER BY co.create_time DESC
