mapper.update:
update platform_saleafter
set status = #{status}
where service_number = #{number}

update platform_saleafter
set status = 2
where status = 1 and service_number = #{number}

mapper.select:
select a.service_number serviceNumber,a.status status,a.order_code orderCode,b.grand_total money, DATE_FORMAT(a.create_time,'%Y-%m-%d') appleTime,a.id id from platform_saleafter a left join client_order b on a.order_code = b.order_code where 1=1
and a.create_time >= #{startTime}
and a.create_time <= #{endTime}
and ( a.service_number like CONCAT('%', #{number}, '%') or a.order_code like CONCAT('%', #{number}, '%') )
order by a.create_time desc

select a.service_number serviceNumber,a.status status,a.order_code orderCode,b.grand_total money, DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i') appleTime,a.id id,b.invoice_status instatus from platform_saleafter a left join client_order b on a.order_code = b.order_code where a.status in (1,2)
and a.create_time >= #{startTime}
and a.create_time <= #{endTime}
and ( a.service_number like CONCAT('%', #{number}, '%') )
order by a.create_time desc
