mapper.select:
select  count(1)
from platform_user_salesman
where status != 0 
and platform_user_code = #{platformUserCode}

    and id != #{id}

SELECT pus.id as recordId, pus.salesman_code AS salesmanCode, pd.dept_name AS deptName, pu.user_name AS userName, pus.create_time AS createTime, pus.invited_client_user_count invitedUserCount, pus.platform_user_code as platformUserCode FROM platform_user_salesman pus JOIN platform_user pu ON pu.user_code = pus.platform_user_code JOIN platform_dept pd ON pd.id = pu.dept_id
pus.status != 0
and pus.create_time >= #{orderStartTime}
and pus.create_time <= #{orderEndTime}
and ( pus.salesman_code LIKE CONCAT('%', #{searchInput}, '%') or pu.user_name LIKE CONCAT('%', #{searchInput}, '%') )
ORDER BY pus.create_time DESC
